
public class Uebung3
{

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int n = 5;
    int i = 0;
    while(i<=n) {
    	int j = 0;
    	int k = 1;
    	int l = 2;
    	int m = 3;
    	int o = 4;
    	int p = 5;
    	if(j==i) {
    		System.out.println(j);
    	} 
    	else {
    		System.out.print(j);
    	}
    	if(i==k) {
    		System.out.println(k);
    	}
    	if(i>k) {
    		System.out.print(k);
    	}
    	if(i==l) {
    		System.out.println(l);
    	}
    	if(i>l){
    		System.out.print(l);
    	}
    	if(i==m){
    		System.out.println(m);
    	}
    	if(i>m){
    		System.out.print(m);
    	}
    	if(i==o){
    		System.out.println(o);
    	}
    	if(i>o){
    		System.out.print(o);
    	}
    	if(i==p) {
    		System.out.println(p);
    	}
    	i=i+1;
    }
	}

}
